import React, { useState } from "react";
import {
  Text,
  TextInput,
  View,
  StyleSheet,
  TouchableOpacity,
} from "react-native";
import Axios from "axios";
import { useNavigation } from "@react-navigation/native";
import Icon from "react-native-vector-icons/FontAwesome";
import { LinearGradient } from "expo-linear-gradient";

export default function RegisterScreen() {
  const navigation = useNavigation();

  const [data, setData] = useState({
    username: "",
    password: "",
    confirmPassword: "",
  });

  const [isValidUser, setIsValidUser] = useState(true);
  const [isValidPassword, setIsValidPassword] = useState(true);

  const register = async () => {
    console.log(
      "username: " +
        data.username +
        " " +
        "password: " +
        data.password +
        " " +
        "confirmPassword: " +
        data.confirmPassword
    );
    try {
      const response = await Axios.post("http://localhost:8000/data/register", {
        username: data.username,
        password: data.password,
        confirmPassword: data.confirmPassword,
      });
      console.log("response", response);
    } catch (error) {
      console.error("Error occurred:", error);
      alert("An error occurred during registration.");
    }
  };

  const setUsername = (val) => {
    setData({
      ...data,
      username: val,
    });
    setIsValidUser(val.length >= 4);
  };

  const setPassword = (val) => {
    setData({
      ...data,
      password: val,
    });
    setIsValidPassword(val.length >= 8);
  };

  const setConfirmPassword = (val) => {
    setData({
      ...data,
      confirmPassword: val,
    });
    setIsValidPassword(val.length >= 8);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={() => navigation.goBack()}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="white" />
      </TouchableOpacity>
      <View style={styles.View1}>
        <Text style={styles.userdata}>Username:</Text>
        <TextInput
          placeholder="Enter Username"
          style={[
            styles.username,
            !isValidUser ? { borderColor: "red" } : null,
          ]}
          onChangeText={(e) => setUsername(e)}
        />
        <Text style={styles.userdata}>Password</Text>
        <TextInput
          placeholder="Password"
          style={[
            styles.username,
            !isValidPassword ? { borderColor: "red" } : null,
          ]}
          onChangeText={(e) => setPassword(e)}
          secureTextEntry
        />
        <Text style={styles.userdata}>Confirm Password</Text>
        <TextInput
          placeholder="Confirm Password"
          style={[
            styles.username,
            !isValidPassword ? { borderColor: "red" } : null,
          ]}
          onChangeText={(e) => setConfirmPassword(e)}
          secureTextEntry
        />
        <TouchableOpacity>
          <LinearGradient
            colors={["#f72855", "#f72855"]}
            style={styles.LoginUser}
          >
            <Text style={styles.Button1} onPress={register}>
              SignUp
            </Text>
          </LinearGradient>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "rgb(255, 190, 123)",
  },
  View1: {
    marginTop: 60,
    backgroundColor: "rgba(255, 190, 123, 0.5)",
    height: 700,
    paddingTop: 80,
    alignItems: "center",
    width: "95%",
    borderRadius: 20,
  },
  backButton: {
    position: "absolute",
    top: 20,
    left: 20,
    zIndex: 1,
  },
  username: {
    fontFamily: "sans-serif",
    alignSelf: "center",
    padding: 18,
    textAlign: "center",
    borderBottomColor: "rgba(100,50,40,0.2)",
    borderBottomWidth: 1,
    borderWidth: 2,
    borderColor: "rgba(100,50,40,0.2)",
    width: "70%",
    backgroundColor: "#d8dbd7",
    fontSize: 20,
    borderRadius: 10,
  },
  userdata: {
    paddingTop: 20,
    paddingBottom: 10,
    alignSelf: "center",
    color: "#eff2e9",
    fontSize: 20,
    justifyContent: "center",
  },
  LoginUser: {
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
    marginTop: 30,
    padding: 15,
    borderRadius: 10,
    width: "70%",
  },
  Button1: {
    fontSize: 20,
    color: "white",
  },
});
