import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, TextInput, Button, ScrollView } from 'react-native';

export default function SProfile({ navigation }) {
  const toggleSCartIcon = () => {
    navigation.navigate('SCartIcon');
  };

  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [products, setProducts] = useState([]);

  const addProduct = () => {
    if (productName && productPrice) {
      const newProduct = {
        name: productName,
        price: productPrice,
      };

      setProducts([...products, newProduct]);
      setProductName('');
      setProductPrice('');
    }
  };

  return (
    <ScrollView style={styles.container}>
      <TouchableOpacity style={styles.cartButton} onPress={toggleSCartIcon}>
        <Text style={styles.cartIcon}>ðŸ›’</Text>
      </TouchableOpacity>

      <Text style={styles.title}>User Profile</Text>
      <View style={styles.profileBox}>
        <View style={styles.iconContainer}>
          <Text style={styles.icon}>ðŸ“·</Text>
        </View>
        <Text style={styles.name}>ChuchuNesssxdxd</Text>
        <Text style={styles.contactNumber}>Contact: +1234567890</Text>
      </View>

      <Text style={styles.productTitle}>Add Product</Text>
      <View style={styles.productForm}>
        <TextInput
          placeholder="Product Name"
          style={styles.input}
          value={productName}
          onChangeText={(text) => setProductName(text)}
        />
        <TextInput
          placeholder="Product Price"
          style={styles.input}
          value={productPrice}
          onChangeText={(text) => setProductPrice(text)}
          keyboardType="numeric"
        />
        <Button title="Add" onPress={addProduct} />
      </View>

      <Text style={styles.productTitle}>Products:</Text>
      <ScrollView>
        {products.map((product, index) => (
          <Text key={index} style={styles.productItem}>
            {product.name} - ${product.price}
          </Text>
        ))}
      </ScrollView>

      <TouchableOpacity style={styles.closeButton} onPress={() => navigation.navigate('SDashboard')}>
        <Text style={styles.closeButtonText}>Close</Text>
      </TouchableOpacity>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  profileBox: {
    borderWidth: 1,
    borderColor: 'gray',
    padding: 20,
    borderRadius: 10,
    alignItems: 'center',
  },
  iconContainer: {
    backgroundColor: 'lightgray',
    width: 100,
    height: 100,
    borderRadius: 50,
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    fontSize: 24,
  },
  name: {
    fontSize: 20,
    fontWeight: 'bold',
    marginTop: 10,
  },
  contactNumber: {
    fontSize: 16,
    marginTop: 5,
  },
  closeButton: {
    marginTop: 20,
    padding: 10,
    backgroundColor: 'blue',
    borderRadius: 5,
  },
  closeButtonText: {
    color: 'white',
    fontSize: 16,
  },
  cartButton: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
  productTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginTop: 20,
  },
  productForm: {
    marginTop: 10,
    marginBottom: 20,
  },
  input: {
    borderWidth: 1,
    borderColor: 'gray',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  },
  productItem: {
    fontSize: 16,
    marginBottom: 5,
  },
});
