import React, { createContext, useContext, useState, useEffect } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const userKey = 'user'; // Replace this with a way to identify the current user.

  useEffect(() => {
    // Load the user-specific cart from local storage on component mount
    const loadCartFromStorage = async () => {
      try {
        const user = localStorage.getItem(userKey);
        if (user) {
          const cartData = localStorage.getItem(`userCart_${user}`);
          if (cartData) {
            setCartItems(JSON.parse(cartData));
          }
        }
      } catch (error) {
        console.error('Error loading cart data:', error);
      }
    };
    loadCartFromStorage();
  }, []);

  const addToCart = (item) => {
    const itemExists = cartItems.some((cartItem) => cartItem.name === item.name);

    if (!itemExists) {
      const updatedCart = [...cartItems, item];
      const user = localStorage.getItem(userKey);
      if (user) {
        localStorage.setItem(`userCart_${user}`, JSON.stringify(updatedCart));
        setCartItems(updatedCart);
      }
    }
  };

  const removeFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.name !== item.name);
    const user = localStorage.getItem(userKey);
    if (user) {
      localStorage.setItem(`userCart_${user}`, JSON.stringify(updatedCart));
      setCartItems(updatedCart);
    }
  };

  const clearCart = () => {
    const user = localStorage.getItem(userKey);
    if (user) {
      localStorage.removeItem(`userCart_${user}`);
      setCartItems([]);
    }
  };

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart, clearCart }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
