import React, { createContext, useContext, useState } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const [userId, setUserId] = useState(null);

  const addToCart = (item) => {
    if (userId) {
      setCartItems([...cartItems, item]);
    }
  };

  const removeFromCart = (item) => {
    if (userId) {
      const updatedCart = cartItems.filter((cartItem) => cartItem.name !== item.name);
      setCartItems(updatedCart);
    }
  };

  const clearCart = () => {
    if (userId) {
      setCartItems([]);
    }
  };

  const setCurrentUser = (user) => {
    setUserId(user.id);
    // When a new user logs in, you may want to clear their cart to start fresh.
    clearCart();
  };

  return (
    <CartContext.Provider
      value={{ cartItems, addToCart, removeFromCart, clearCart, setCurrentUser }}
    >
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
