import React from 'react';
import { TouchableOpacity, StyleSheet, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { useCart } from '../../CartContext'; 
import Icon from 'react-native-vector-icons/FontAwesome';

export default function CartIcon() {
  const navigation = useNavigation();
  const { cartItems } = useCart(); 

  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price.slice(1)), 0);
  };

  return (
    <>
      <TouchableOpacity
        onPress={() => navigation.navigate('Dashboard')} 
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>
      <TouchableOpacity
        onPress={() => navigation.navigate('Dashboard')} 
        style={styles.cartButton}
      >
        <Text style={styles.cartIcon}>
          ðŸ›’
          {cartItems.length > 0 && (
            <Text style={styles.cartItemCount}>{cartItems.length}</Text>
          )}
        </Text>
        <Text style={styles.cartTotal}>Total: ${calculateTotalPrice().toFixed(2)}</Text> {/* Add this line */}
      </TouchableOpacity>
    </>
  );
}
const styles = StyleSheet.create({
  backButton: {
    position: 'absolute',
    top: 20,
    left: 20,
    backgroundColor: 'transparent',
  },
  cartButton: {
    position: 'absolute',
    right: 20,
    top: 5,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
    marginTop: 10,
  },
  cartItemCount: {
    position: 'absolute',
    top: 0,
    right: 0,
    backgroundColor: 'red',
    color: 'white',
    borderRadius: 10,
    paddingHorizontal: 4, 
    paddingVertical: 2,
    fontSize: 10,
  },
});
