import React from 'react';
import { TouchableOpacity, Text, StyleSheet } from 'react-native';

export default function CartIcon({ onPress, cartItems, calculateTotalPrice }) {
  return (
    <TouchableOpacity style={styles.cartIconContainer} onPress={onPress}>
      <Text style={styles.cartIcon}>ðŸ›’</Text>
      <Text style={styles.cartItemCount}>{cartItems.length}</Text>
      <Text style={styles.cartTotal}>${calculateTotalPrice().toFixed(2)}</Text>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
  cartItemCount: {
    position: 'absolute',
    top: -5,
    right: -5,
    backgroundColor: 'red',
    borderRadius: 50,
    width: 20,
    height: 20,
    color: 'white',
    textAlign: 'center',
  },
  cartTotal: {
    fontSize: 12,
    textAlign: 'center',
  },
});
