import React, { createContext, useContext, useState } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const [authenticatedUser, setAuthenticatedUser] = useState(null);

  const addToCart = (item) => {
    if (authenticatedUser) {
      setCartItems([...cartItems, item]);
    } else {
      // Handle the case when the user is not authenticated, e.g., show a login prompt.
      console.log('Please log in to add items to the cart.');
    }
  };

  const removeFromCart = (item) => {
    if (authenticatedUser) {
      const indexOfItemToRemove = cartItems.findIndex((cartItem) => cartItem.name === item.name);
      if (indexOfItemToRemove !== -1) {
        const updatedCart = [...cartItems];
        updatedCart.splice(indexOfItemToRemove, 1);
        setCartItems(updatedCart);
      }
    } else {
      // Handle the case when the user is not authenticated, e.g., show a login prompt.
      console.log('Please log in to remove items from the cart.');
    }
  };

  const clearCart = () => {
    if (authenticatedUser) {
      setCartItems([]);
    } else {
      // Handle the case when the user is not authenticated, e.g., show a login prompt.
      console.log('Please log in to clear the cart.');
    }
  };

  return (
    <CartContext.Provider
      value={{ cartItems, addToCart, removeFromCart, clearCart, authenticatedUser, setAuthenticatedUser }}
    >
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  return useContext(CartContext);
}
