import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

export default function CartIcon({ onPress, cartItems, calculateTotalPrice }) {
  return (
    <View>
      <TouchableOpacity style={styles.cartIconContainer} onPress={onPress}>
        <Text style={styles.cartIcon}>ðŸ›’</Text>
      </TouchableOpacity>
      {cartItems.length > 0 && (
        <View style={styles.cartTotalContainer}>
          <Text style={styles.totalPrice}>Total: ${calculateTotalPrice().toFixed(2)}</Text>
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
  },
  cartIcon: {
    fontSize: 24,
  },
  cartTotalContainer: {
    position: 'absolute',
    top: 70,
    right: 20,
    backgroundColor: 'transparent',
  },
  totalPrice: {
    fontSize: 18,
    fontWeight: 'bold',
  },
});
