import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableHighlight } from 'react-native';
import CartIcon from './CartIcon';
export default function McDonaldsMenu({ navigation }) {
  const menuItems = [
    { name: 'Big Mac', price: '$5.99' },
    { name: 'Chicken McNuggets', price: '$4.99' },
    { name: 'Quarter Pounder', price: '$6.99' },
    { name: 'French Fries', price: '$2.99' },
  ];

  const [pressedMenuItem, setPressedMenuItem] = useState(null);

  return (
    <ScrollView style={styles.foodItemsContainer}>
      <Text style={styles.sectionTitle}>McDonald's Menu</Text>
      {menuItems.map((item, itemIndex) => (
        <TouchableHighlight
          key={itemIndex}
          style={[
            styles.foodItem,
            { backgroundColor: pressedMenuItem === item.name ? '#FFD700' : '#fff' },
          ]}
          underlayColor="#FFD700"
          onPress={() => {
            navigation.navigate('FoodItemDetails', { item });
          }}
          onShowUnderlay={() => setPressedMenuItem(item.name)}
          onHideUnderlay={() => setPressedMenuItem(null)}
        >
          <>
            <Text style={styles.foodItemName}>{item.name}</Text>
            <Text style={styles.foodItemPrice}>{item.price}</Text>
          </>
        </TouchableHighlight>
      ))}
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  foodItemsContainer: {
    flex: 1,
    paddingHorizontal: 20,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  foodItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 15,
    marginBottom: 10,
    borderRadius: 8,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 2,
    elevation: 2,
  },
  foodItemName: {
    fontSize: 18,
  },
  foodItemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
});
