  import React, { useState } from 'react';
  import { View, Text, StyleSheet, ScrollView, TouchableHighlight, TouchableOpacity } from 'react-native';
  import Icon from 'react-native-vector-icons/FontAwesome';
  import { useNavigation } from '@react-navigation/native';

  export default function Subway() {
    const navigation = useNavigation();

    const menuItems = [
      { name: 'Italian BMT', price: '$6.99' },
      { name: 'Chicken Teriyaki', price: '$7.49' },
      { name: 'Veggie Delite', price: '$5.99' },
    ];

    const [pressedMenuItem, setPressedMenuItem] = useState(null);
    const [cartItems, setCartItems] = useState([]); 
    const [cartVisible, setCartVisible] = useState(true); // Set cartVisible to true by default

    const addToCart = (item) => {
      setCartItems([...cartItems, item]); 
    };
    
    const toggleCartIcon = () => {
      navigation.navigate('CartIcon');
    };

    const removeFromCart = (item) => {
      const updatedCart = cartItems.filter((cartItem) => cartItem.name !== item.name); 
      setCartItems(updatedCart);
    };

    const calculateTotalPrice = () => {
      return cartItems.reduce((total, item) => total + parseFloat(item.price.slice(1)), 0);
    };

    return (
      <View style={styles.container}>
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}
        >
          <Icon name="arrow-left" size={24} color="black" />
        </TouchableOpacity>
        <TouchableOpacity style={styles.cartButton} onPress={toggleCartIcon}>
          <CartIcon onPress={toggleCartIcon} cartItems={cartItems} totalPrice={calculateTotalPrice()}/>
        </TouchableOpacity>

        <Text style={styles.sectionTitle}>Subway Menu</Text>
        <ScrollView style={styles.foodItemsContainer}>
          {menuItems.map((item, itemIndex) => (
            <TouchableHighlight
              key={itemIndex}
              style={[
                styles.foodItem,
                { backgroundColor: pressedMenuItem === item.name ? '#FFD700' : '#fff' },
              ]}
              underlayColor="#FFD700"
              onPress={() => {
                navigation.navigate('FoodItemDetails', { item });
              }}
              onShowUnderlay={() => setPressedMenuItem(item.name)}
              onHideUnderlay={() => setPressedMenuItem(null)}
            >
              <>
                <Text style={styles.foodItemName}>{item.name}</Text>
                <Text style={styles.foodItemPrice}>{item.price}</Text>
                <TouchableOpacity
                  style={styles.addToCartButton}
                  onPress={() => addToCart(item)}
                >
                  <Text style={styles.addToCartButtonText}>Add to Cart</Text>
                </TouchableOpacity>
              </>
            </TouchableHighlight>
          ))}
        </ScrollView>

        {cartVisible && (
          <View style={styles.cartContainer}>
            <Text style={styles.cartTitle}>Cart</Text>
            {cartItems.map((cartItem, cartIndex) => (
              <View key={cartIndex} style={styles.cartItem}>
                <Text style={styles.cartItemName}>{cartItem.name}</Text>
                <Text style={styles.cartItemPrice}>{cartItem.price}</Text>
                <TouchableOpacity
                  style={styles.removeFromCartButton}
                  onPress={() => removeFromCart(cartItem)}
                >
                  <Text style={styles.removeFromCartButtonText}>Remove</Text>
                </TouchableOpacity>
              </View>
            ))}
            <Text style={styles.totalPrice}>Total: ${calculateTotalPrice().toFixed(2)}</Text>
          </View>
        )}
      </View>
    );
  }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      marginTop: 19,
      paddingHorizontal: 20,
      justifyContent: 'center',
    },
    sectionTitle: {
      fontSize: 24,
      fontWeight: 'bold',
      marginTop: 10,
    },
    foodItemsContainer: {
      flex: 1,
    },
    foodItem: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      backgroundColor: '#fff',
      padding: 15,
      marginTop: 50,
      borderRadius: 8,
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.3,
      shadowRadius: 2,
      elevation: 2,
      width: '100%', 
    },
    foodItemName: {
      fontSize: 18,
    },
    foodItemPrice: {
      fontSize: 16,
      fontWeight: 'bold',
    },
    cartIcon: {
      position: 'absolute',
      right: 20,
      backgroundColor: 'transparent',
      fontSize: 24,
      marginTop: 10,
    },
    cartButton: {
      position: 'absolute',
      right: 20,
      top: 5,
      backgroundColor: 'transparent',
    },
    cartContainer: {
      backgroundColor: '#fff',
      padding: 20,
      marginTop: 20,
      borderRadius: 8,
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.3,
      shadowRadius: 2,
      elevation: 2,
    },
    cartTitle: {
      fontSize: 20,
      fontWeight: 'bold',
      marginBottom: 10,
    },
    cartItem: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: 10,
    },
    cartItemName: {
      fontSize: 16,
    },
    cartItemPrice: {
      fontSize: 16,
      fontWeight: 'bold',
    },
    removeFromCartButton: {
      backgroundColor: '#FF5733',
      padding: 5,
      borderRadius: 5,
    },
    removeFromCartButtonText: {
      color: 'white',
      fontSize: 14,
    },
    totalPrice: {
      fontSize: 18,
      fontWeight: 'bold',
      marginTop: 10,
    },
    // Style for the "Add to Cart" button
    addToCartButton: {
      backgroundColor: '#007AFF', // Change the background color
      padding: 10, // Adjust padding as needed
      borderRadius: 5, // Add border radius
      marginTop: 5, // Add some top margin
    },
    addToCartButtonText: {
      color: 'white', // Change text color
      fontSize: 14,
      textAlign: 'center', // Center the text
    },
  });
