import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Icon from 'react-native-vector-icons/FontAwesome';
import McDonaldsMenu from '../components/fastfood/McDonaldsMenu';

export default function Dashboard({ navigation }) {
  return (
    <Background>
      <Logo />
      <View style={styles.headerContainer}>
        <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
      </View>

      <Text style={styles.brandName}>McDonald's</Text>
      <TouchableOpacity
        style={styles.brandIcon}
        onPress={() => navigation.navigate('McDonaldsMenu')}
      >
        <Icon name="arrow-right" size={30} color="#000" />
      </TouchableOpacity>

      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.brandIcon}
        onPress={() => navigation.navigate('McDonaldsMenu')}
      >
        <Icon name="arrow-right" size={30} color="#000" />
      </TouchableOpacity>

      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.brandIcon}
        onPress={() => navigation.navigate('McDonaldsMenu')}
      >
        <Icon name="arrow-right" size={30} color="#000" />
      </TouchableOpacity>

      <TouchableOpacity style={styles.cartButton}>
        <Icon name="shopping-cart" size={30} color="#fff" />
      </TouchableOpacity>

    </Background>
  );
}

const styles = StyleSheet.create({
  headerContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center', // Center-align the text
    marginBottom: 20, // Add some spacing below the text
  },
  brandContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  brandName: {
    fontSize: 24,
    fontWeight: 'bold',
    marginRight: 10,
  },
  brandIcon: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  cartButton: {
    backgroundColor: 'blue',
    opacity: 0.6,
    alignItems: 'center',
    justifyContent: 'center',
    width: 60,
    height: 60,
    borderRadius: 30,
    position: 'absolute',
    bottom: 20,
    right: 20,
  },
});
