import React from 'react';
import { TouchableOpacity, Text, StyleSheet, View } from 'react-native';

export default function CartIcon({ onPress, cartItems, total }) {
  return (
    <TouchableOpacity style={styles.cartIconContainer} onPress={onPress}>
      <Text style={styles.cartIcon}>ðŸ›’</Text>
      {cartItems.length > 0 && (
        <View style={styles.cartInfo}>
          <Text style={styles.cartItemCount}>{cartItems.length} items</Text>
          <Text style={styles.cartTotal}>Total: ${total.toFixed(2)}</Text>
        </View>
      )}
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  cartIconContainer: {
    position: 'absolute',
    top: 20,
    right: 20,
    backgroundColor: 'transparent',
    flexDirection: 'row',
    alignItems: 'center',
  },
  cartIcon: {
    fontSize: 24,
    marginRight: 5,
  },
  cartInfo: {
    marginLeft: 5,
    justifyContent: 'center',
    alignItems: 'flex-end',
  },
  cartItemCount: {
    fontSize: 12,
    fontWeight: 'bold',
  },
  cartTotal: {
    fontSize: 14,
    fontWeight: 'bold',
  },
});
