import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableHighlight, TouchableOpacity } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useNavigation } from '@react-navigation/native';

export default function Subway() {
  const navigation = useNavigation();

  const menuItems = [
    { name: 'Italian BMT', price: '$6.99' },
    { name: 'Chicken Teriyaki', price: '$7.49' },
    { name: 'Veggie Delite', price: '$5.99' },
  ];

  const [pressedMenuItem, setPressedMenuItem] = useState(null);
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem.name !== item.name);
    setCartItems(updatedCart);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        onPress={() => navigation.goBack()}
        style={styles.backButton}
      >
        <Icon name="arrow-left" size={24} color="black" />
      </TouchableOpacity>
      
      {/* Cart icon */}
      <TouchableOpacity style={styles.cartButton} onPress={toggleCartIcon}>
        <Text style={styles.cartIcon}>ðŸ›’</Text>
      </TouchableOpacity>

      <Text style={styles.sectionTitle}>Subway Menu</Text>
      <ScrollView style={styles.foodItemsContainer}>
        {menuItems.map((item, itemIndex) => (
          <TouchableHighlight
            key={itemIndex}
            style={[
              styles.foodItem,
              { backgroundColor: pressedMenuItem === item.name ? '#FFD700' : '#fff' },
            ]}
            underlayColor="#FFD700"
            onPress={() => {
              navigation.navigate('FoodItemDetails', { item });
            }}
            onShowUnderlay={() => setPressedMenuItem(item.name)}
            onHideUnderlay={() => setPressedMenuItem(null)}
          >
            <>
              <Text style={styles.foodItemName}>{item.name}</Text>
              <Text style={styles.foodItemPrice}>{item.price}</Text>
              <TouchableOpacity
                style={styles.addToCartButton}
                onPress={() => addToCart(item)}
              >
                <Text style={styles.addToCartButtonText}>Add to Cart</Text>
              </TouchableOpacity>
            </>
          </TouchableHighlight>
        ))}
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  // ... existing styles ...
});
