import React, { useState } from "react";
import {
  Text,
  TextInput,
  View,
  StyleSheet,
  TouchableOpacity,
  Animated,
  TouchableWithoutFeedback,
  ScrollView,
  BackHandler, // Import BackHandler
  Alert, // Import Alert
} from "react-native";
import Background from "../components/Background";
import Logo from "../components/Logo";
import Header from "../components/Header";
import Icon from "react-native-vector-icons/FontAwesome";

export default function SDashboard({ navigation }) {
  const [scaleValue] = useState(new Animated.Value(1));

  const toggleSProfileIcon = () => {
    navigation.navigate("SProfileIcon");
  };

  const toggleSCartIcon = () => {
    navigation.navigate("SCartIcon");
  };

  const handlePress = (menu) => {
    Animated.sequence([
      Animated.timing(scaleValue, { toValue: 0.9, duration: 100, useNativeDriver: true }),
      Animated.timing(scaleValue, { toValue: 1, duration: 100, useNativeDriver: true }),
    ]).start(() => {
      if (menu === "SMcDonalds") {
        navigation.navigate(menu + "Menu");
      } else if (menu === "SSubway") {
        navigation.navigate(menu);
      } else if (menu === "SKFC") {
        navigation.navigate(menu);
      }
    });
  };

  const cartItems = [];

  // Handle the back button press
  const handleBackButton = () => {
    Alert.alert(
      "Exit App",
      "Do you want to exit the app?",
      [
        {
          text: "Cancel",
          onPress: () => null,
          style: "cancel",
        },
        { text: "OK", onPress: () => BackHandler.exitApp() }, // Exit the app when OK is pressed
      ],
      { cancelable: false }
    );
    return true;
  };

  // Register the back button handler
  React.useEffect(() => {
    BackHandler.addEventListener("hardwareBackPress", handleBackButton);
    return () => {
      BackHandler.removeEventListener("hardwareBackPress", handleBackButton);
    };
  }, []);

  const handleLogout = () => {
    // Clear the text fields and navigate to the login screen
    // You may need to replace 'LoginScreen' with the actual name of your login screen component
    navigation.navigate("LoginScreen");
  };

  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <Background>
        <Logo />
        <View style={styles.headerContainer}>
          <Header style={styles.welcomeText}>Welcome to APPLEGO Express</Header>
        </View>

        <TouchableOpacity style={styles.profileButton} onPress={toggleSProfileIcon}>
          <View style={styles.iconCircle}>
            <Icon name="user" size={30} color="#FFF" />
          </View>
        </TouchableOpacity>

        <TouchableWithoutFeedback onPress={() => handlePress("SMcDonalds")}>
          <Animated.View style={[styles.brandButton, { transform: [{ scale: scaleValue }] }]}>
            <View>
              <Text style={styles.brandButtonText}>McDonald's</Text>
              <Text style={styles.salesQuote}>
                "I'm lovin' it!" - McDonald's
              </Text>
              <Text style={styles.additionalInfo}>
                Serving Happiness Since 1955
              </Text>
              <Text style={styles.additionalInfo}>
                Find us at 1234 Fast Food Lane
              </Text>
            </View>
            <Icon name="arrow-right" size={30} color="#007BFF" />
          </Animated.View>
        </TouchableWithoutFeedback>

        {/* Other brand buttons */}
        {/* ... */}

        <TouchableOpacity style={styles.cartButton} onPress={toggleSCartIcon}>
          <View style={styles.iconCircle}>
            <Text style={styles.cartIcon}>ðŸ›’</Text>
            {cartItems.length > 0 && (
              <Text style={styles.cartItemCount}>{cartItems.length}</Text>
            )}
          </View>
        </TouchableOpacity>

        {/* Logout button */}
        <TouchableOpacity style={styles.logoutButton} onPress={handleLogout}>
          <Text style={styles.logoutText}>Logout</Text>
        </TouchableOpacity>
      </Background>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  // ... (existing styles)

  // Add styles for the logout button
  logoutButton: {
    position: "absolute",
    bottom: 20,
    right: 20,
    backgroundColor: "#f72855",
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 10,
    elevation: 2,
  },
  logoutText: {
    color: "white",
    fontSize: 18,
    fontWeight: "bold",
  },
});
